import React, { useState } from 'react';  
import './App.css';

function App() {
  const [state, setstate] = useState(0)

 const Decrement =()=>{
    if(!state<=0){
      setstate((state = 0))
    }else{
      setstate((state - 1))
    }
 }

 const Increment =()=>{
   setstate((state + 1))
 }
  return (
    <div className="container ">
         <div>
              <button onClick={Decrement}>-</button>
              <span>Count :{state}</span>
              <button onClick={Increment}>+</button>
         </div>
    </div>
  );
}

export default App;
